sections.css:
$font__serif: "Freight Text Pro", freight-text-pro, Georgia, serif;
$font__sans-serif: "Proxima Nova", proxima-nova, Helvetica, Arial, sans-serif;
$font__display: lust-display, $font__serif;

$font__main: $font__serif;
$font__accent: $font__sans-serif;
$font__headings: $font__display;

$font__code: Monaco, Consolas, "Andale Mono", "DejaVu Sans Mono", monospace;
$font__pre: "Courier 10 Pitch", Courier, monospace;


$font__line-height-body: 1.6;
$font__line-height-pre: 1.6;

h1, h2, h3, h4, h5, h6 {
	clear: both;
	font-weight: 500;
	line-height: 1.2;
	margin: 0 0 $size__site-gutter/2;

	a {
		text-decoration: none;
	}
}

h1 {
	color: $color__headings;
	letter-spacing: -0.03em;
	line-height: 1;
	font-family: $font__display;
	@include font-size(3.6);

	@include tablet {
		@include font-size(7);
	}

	a {
		color: $color__headings;
	}
}

h2 {
	color: $color__headings;
	font-style: italic;
	@include font-size(1.8);
	font-weight: 100;
	line-height: 1.2;
	margin-top: 3em;

	@include tablet {
		@include font-size(2.4);
	}
}

h3 {
	color: $color__headings;
	font-family: $font__accent;
	@include font-size(0.8);
	font-weight: 600;
	letter-spacing: 2px;
	margin: 0;
	text-transform: uppercase;

	@include tablet {
		@include font-size(0.9);
	}
}

/* Body copy */
p {
	margin: 0 0 $size__site-gutter*2;
}

.cap,
.single:not(.single-wanderlist-location) .entry-content p:first-of-type {

	&::first-letter {
		color: $color__neutral;
		display: block;
		float: left;
		@include font-size(7);
		font-family: $font__main;
		font-weight: 700;
		//initial-letter: 3; // Support too patchy right now. :(
		line-height: 0.6;
		margin: 14px 5px 0 -5px;

		@include tablet {
			@include font-size(15);
			// initial-letter: 4;
			margin-top: 16px;
		}
	}

	&::first-line {
		font-variant: small-caps;
		letter-spacing: 2px;
	}
}

dfn, cite, em, i {
	font-style: italic;
}

blockquote {
	margin: 0 1.5em;
	border-top: 1px solid lighten($color__text-main, 50%);
  color: $silver;
  display: block;
  @include font-size(1.2);
  font-style: italic;
  margin: 0.25em auto;
  padding-top: 0.5em;
  text-align: left;

  @include tablet {
    @include font-size(1.4);
  }
}

address {
	margin: 0 0 1.5em;
}

pre {
	background: $color__background-pre;
	font-family: $font__pre;
	@include font-size(0.9375);
	line-height: $font__line-height-pre;
	margin-bottom: 1.6em;
	max-width: 100%;
	overflow: auto;
	padding: 1.6em;
}

code, kbd, tt, var {
	font-family: $font__code;
	@include font-size(0.9375);
}

abbr, acronym {
	border-bottom: 1px dotted $color__border-abbr;
	cursor: help;
}

mark, ins {
	background: $color__background-ins;
	text-decoration: none;
}

big {
	font-size: 125%;
}

strong {
	background: $color__highlight;
	color: $color__text-main;
	padding: 0 5px;
}

.caps {
	font-variant: small-caps;
	letter-spacing: 1px;
	text-transform: lowercase;
}

@mixin card() {
	@include box-shadow;
	background: $color__white;
	margin: 0 0 $size__site-gutter/2 0;
	padding: $size__site-gutter/2;
}

@mixin small-caps() {
	font-family: $font__accent;
	@include font-size(0.8);
	font-weight: 600;
	letter-spacing: 1px;
	margin: 0;
	text-transform: uppercase;

	@include tablet {
		@include font-size(0.9);
	}
}


// A skewed edge, used to make containers look funkier.
// Courtesy of http://www.hongkiat.com/blog/skewed-edges-css/
@mixin angled-edge($position, $angle){
	width: 100%;
	position: relative;

	&::before,
	&::after {
		content: '';
		display: block;
		width: 100%;
		height: 100%;
		left: 0;
		right: 0;
		position: absolute;
		background: inherit;
		z-index: -1;
		transition: ease all .5s;
	}

    @if $position == 'topleft' {
		&::before {
        	top: 0;
        	transform-origin: right top;
        	transform: skewY($angle);
      	}

		&::after {
			display: none;
		}
	}

    @if $position == 'topright' {
		&::before {
	        top: 0;
	        transform-origin: left top;
	        transform: skewY(-$angle);
		}

		&::after {
			display: none;
		}
    }

    @if $position == 'bottomleft' {
		&::after {
	        bottom: 0;
	        transform-origin: right bottom;
	        transform: skewY(-$angle);
      	}

		&::before {
			display: none;
		}
	}

    @if $position == 'bottomright' {
		&::after {
	        bottom: 0;
	        transform-origin: left bottom;
	        transform: skewY($angle);
      	}

		&::before {
			display: none;
		}
    }
}

// Apply a rainbowy gradient to text elements.
@mixin text-gradient {
	background: $color__primary;  /* fallback for old browsers */
	background: -webkit-linear-gradient(to bottom, $color__accent, $color__primary);  /* Chrome 10-25, Safari 5.1-6 */
	background: linear-gradient(to bottom, $color__accent, $color__primary); /* W3C, IE 10+/ Edge, Firefox 16+, Chrome 26+, Opera 12+, Safari 7+ */
	-webkit-background-clip: text;
	-webkit-text-fill-color: transparent;
}

// Styling for page headers, used for pages as well as single entry pages.
@mixin page-header {
	@include angled-edge(bottomright, 4deg);
	background: $color__black;
	margin: 0 -#{$size__site-gutter/2};
	width: calc(100% + #{$size__site-gutter/2});
	position: relative;
	padding: $size__site-gutter;
	z-index: -2;

	@include tablet {
		padding: $size__site-gutter*2;
	}

	.page-title,
	.entry-title {
		@include text-gradient;
		padding: 10px 0 35px; // Avoids text being cut off in FF.
	}
}

